CREATE TABLE `systwone`.`fmatches` ( `id` INT NOT NULL AUTO_INCREMENT , `Hplayer` VARCHAR(100) NOT NULL , `Aplayer` VARCHAR(100) NOT NULL , `Hteam` VARCHAR(100) NOT NULL , `Ateam` VARCHAR(100) NOT NULL , `Hscore` INT NOT NULL , `Ascore` INT NOT NULL , `tomatch` VARCHAR(100) NOT NULL , `payers` VARCHAR(100) NOT NULL , `tarehe` VARCHAR(100) NOT NULL , `wakati` VARCHAR(100) NOT NULL , `debt` INT NOT NULL , `matchid` VARCHAR(100) NOT NULL , `matchstatus` INT NOT NULL DEFAULT '0' , PRIMARY KEY (`id`)) ENGINE = InnoDB; 